sql.where:
AND 1=1


        AND a.status = #{status}


        AND a.status >= 0



    AND a.merchant_id = #{merchantId}


    AND a.update_time >= #{startDate}


    AND a.update_time <= #{endDate}


    AND YEAR (a.update_time) = #{currentYear}


    AND MONTH (a.update_time) = #{currentMonth}


    AND DAY (a.update_time) = #{currentDay}

